@import url("https://fonts.googleapis.com/css2?family=Inter:wght@500&display=swap");
.button {
  border: none;
  outline: none;
  padding: 12px 0;
  text-decoration: none;
  font-family: "Inter", sans-serif;
}
.button_prime {
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: fit-content;
  background-color: #3369F3;
  border-radius: 8px;
}
.button_prime > span {
  font-size: 13px;
  color: #fff;
}
.button_second {
  background-color: transparent;
  color: #3369F3;
  font-size: 11px;
}

.modal {
  overflow: hidden;
  z-index: 9999;
  position: fixed;
  top: 0;
  left: 0;
  justify-content: center;
  align-items: center;
  height: 100%;
  width: 100%;
  display: none;
  background-color: rgba(0, 0, 0, 0.3);
}
.modal_visible {
  display: flex;
}
.modal__container {
  margin-left: 64px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  width: 340px;
  height: 257px;
  border-radius: 16px;
  background-color: #fff;
}

.labels {
  width: 100%;
  margin-bottom: 160px;
}

.login {
  width: auto;
  box-sizing: border-box;
  padding: 50px 30px 30px;
}
.login__label {
  display: flex;
  flex-direction: column;
  font-family: "Inter", sans-serif;
}
.login__label__span {
  font-size: 9px;
  color: #999999;
}
.login__form {
  width: 340px;
  height: fit-content;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  border-radius: 12px;
  box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.14);
}
.login__form__label {
  font-size: 20px;
  color: #1e1e1e;
  font-family: "Inter", sans-serif;
}

body, html {
  width: 100%;
  height: 100%;
  margin: 0;
}

main {
  width: 100%;
  height: 100%;
}

.container {
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
}

.container_flex {
  display: flex;
  width: 100%;
  height: 100%;
}
